sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    if (!newValue == "") {
        g_form.setSectionDisplay("device_detauls", true);
        g_form.setSectionDisplay("delivery_details", true);

		g_form.getReference("u_device_name",getDetails);//call back function
    }

	function getDetails(details){
		g_form.setValue("u_model",details.model_number);
		g_form.setValue("u_price_9",details.u_price_2);
		g_form.setValue("u_device_description",details.short_description);
		g_form.setValue("u_warranty_expiration",details.warranty_expiration);
	}

}
